                         uv = tempuv*vec2(-1.,1.);
                     }
                     uv = rotate2D(uv,.833 * PI * float(k)); // 6symmetries:0.833 , multiply with K to rotate
                } else {
                    uv = rotate2D(uv,anglePie);
                } 

                //uv = _uv; // this just removes rotations
                // //distorting rotated uv
                 float x = uv.x;
                // //float m = (x-.1)*(x+.1); // this makes symmetry drift
                 float mx = x*x;
                 float y = uv.y + (mx*distortionVal);
                 float my = y*y;
                 x = x + (my*distortionVal); // distorts only Y axis bc of symmetyr
                 uv = vec2(x,y);

                ///Drawing part
                float o1 = lineDist(uv,opos,last,.02+ls*boneWeight); // bone lines
                //float o1 = lineDist( uv,rotate2D(opos,angleIN*PI),rotate2D(last,angleIN*PI),.02+ls*boneWeight); // rotate wholearm
                //float o2 = length( uv - opos) - (.01+bs*jointWeight); // joint balls
                //float o = min(o1,o2);
                float o = o1;
                d = smoothMerge(d,o,.01 + ma * metaBlur);   // Merge shapes    
                d = min(d,o);


            } 
            //la = a;
            last = opos;        
        }
    }


    vec3 col = draw_distance(d,_uv); // doesn't use uv
    //vec3 col = draw_distance(d);

    outColor = vec4(col,1.0); // Output
}

{{MODULES_HEAD}}
IN vec3 vPosition;
IN vec2 attrTexCoord;
IN vec3 attrVertNormal;
IN vec3 attrTangent,attrBiTangent;
IN float attrVertIndex;
OUT vec2 texCoord;
OUT vec3 norm;
UNI mat4 projMatrix;
UNI mat4 viewMatrix;
UNI mat4 modelMatrix;
void main()
{
    texCoord=attrTexCoord;
    norm=attrVertNormal;
    vec4 pos=vec4(vPosition,  1.0);
    vec3 tangent=attrTangent;
    vec3 bitangent=attrBiTangent;
    mat4 mMatrix=modelMatrix;
    {{MODULE_VERTEX_POSITION}}
    gl_Position = projMatrix * (viewMatrix*mMatrix) * pos;
}
